import { WorldStateSyncStatusSchema } from './world_state.js';
export const MAX_LOGS_PER_TAG = 10;
const MAX_SIGNATURES_PER_REGISTER_CALL = 100;
const MAX_SIGNATURE_LEN = 10000;
