    getLogsByTags: z.function().args(z.array(schemas.Fr).max(MAX_RPC_LEN), optional(z.number().gte(1).lte(MAX_LOGS_PER_TAG).default(MAX_LOGS_PER_TAG))).returns(z.array(z.array(TxScopedL2Log.schema))),
    sendTx: z.function().args(Tx.schema).returns(z.void()),
